using System;
using System.Collections.Generic;
using System.Linq;
using Xamarin.Forms;

namespace Android.Glide.Sample
{
	public partial class ListViewOfDoom : ContentPage
	{
		public ListViewOfDoom ()
		{
			InitializeComponent ();

			BindingContext = GetImageUrls (100).ToArray ();
		}

		readonly Random random = new Random ();
		const string Url = "https://img.resized.-trump-wwe-irish-post.png?rand=";

		IEnumerable<ImageSource> GetImageUrls (int count)
		{
			for (int i = 0; i < count; i++) {
				yield return ImageSource.FromUri (new Uri (Url + random.Next ()));
			}
		}
	}
}
